import React , {useState} from "react";
import { useNavigate } from "react-router-dom";

const axios = require('axios').default;



function Login(){
    const [user , setUser] = useState({
        email: "",
        password: ""
    });

    function updateUser(event){
        const { name, value } = event.target;

        setUser(prevValue => {
            return {
            ...prevValue,
            [name] : value
         }});
    }

     const navigate = useNavigate();

   

async function sendUserData(){

    const URL = 'http://localhost:4000/users/login'; // for Local

  
    axios.post(URL, {
      email: user.email,
      password: user.password,
    })
    .then((res) => {
      if (res.data){
        console.log(res.data);
        alert("user logged in");
        navigate("/main");
        setUser({email: "",password: "" , isCoach: false});
      }else{
        alert("user didn't logged in");
        setUser({email: "",password: "" , isCoach: false});
      }
    });
  }

    return  <div className="login-form">  
                <div className="card">
                    <form className="card-body" action="/login" method="POST">
                        <div className="form-group">
                            <label>Email:</label>
                            <input onChange={updateUser} type="email" name="email" id="email"></input>
                        </div>
                        <div className="form-group">
                            <label>Password:</label>
                            <input onChange={updateUser} type="password" name="password" id="password"></input>
                        </div>
                        <div className="text-center">
                            <button onClick={sendUserData} className="btn btn-primary " type="submit">Login</button>
                        </div>
                  </form>
                </div>
            </div>
}




export default Login;
