import React from 'react';
import {
    MDBCard,
    MDBCardBody,
    MDBCardTitle,
    MDBCardText,
    MDBCardImage,
    MDBBtn,
    MDBRipple
} from 'mdb-react-ui-kit';

export default function Card(props) {
    const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday"];

    return (
        <MDBCard>
            <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
                <MDBCardImage src='https://mdbootstrap.com/img/new/standard/nature/111.webp' fluid alt='...' />
                <a>
                    <div className='mask'>fdg</div>
                </a>
            </MDBRipple>
            <MDBCardBody>
                <MDBCardTitle>{props.coach.username.split("@")[0]}</MDBCardTitle>
                <MDBCardText>
                    <p>Swimming Style:<br /> {props.coach.swimmingStyle.map(style => { return style + " " })}</p>
                    <p>Work Days:<br /> {props.coach.workDays.map(day => { return days[day - 1] + " " })}</p>
                    <p>Work Hours:<br /> {props.coach.workHours[0] + ":00 - " + props.coach.workHours[1] + ":00"}</p>
                </MDBCardText>
            </MDBCardBody>
        </MDBCard>
    );
}
