import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
const axios = require('axios').default;



function NavBar() {
    const navigate = useNavigate();
    // var isLoggedIn = localStorage.getItem('isLoggedIn');
    const [isLogged, setLogged] = useState(false);
    // if (!isLoggedIn)
    //     isLoggedIn = false;
    // console.log(isLoggedIn);

    useEffect(() => {
        // isLoggedIn = localStorage.getItem('isLoggedIn') === false;
        setLogged(localStorage.getItem('isLoggedIn') === true);
    }, 1000);


    function logOut() {
        // document.cookie = `isLoggedIn=false; scure`
        const URL = 'http://localhost:4000/users/logout'; // for Local
        axios.post(URL)
            .then((res) => {
                console.log(res.data);
                const legged = localStorage.setItem('isLoggedIn', 'false');
                setLogged(false);
                navigate("/login");
            });
    }

    return <div>
        <nav className="navbar">

            <span className="navbar-brand">Poolit <a href="/"><img src="photos/swimming-logo.ico" alt="Poolit Logo"></img></a></span>
            <div className="">
                <ul className="navbar-nav ml-auto">
                    <li className="nav-item">
                        {/* <span className="ml-auto btn"><a href="/login">Login </a></span>
                        <span className="navbar-right btn"><a href="/register"> Register</a></span>
                        <span className="navbar-right "><button type="submit" className="btn" onClick={logOut}>Logout</button></span> */}
                        {!isLogged ? <span className="ml-auto"><a href="/login">Login </a></span> : ""}
                        {!isLogged ? <span className="navbar-right "><a href="/register"> Register</a></span> : ""}
                        {isLogged ? <span className="navbar-right "><button type="submit" className="btn" onClick={logOut}>Logout</button></span> : ""}
                    </li>
                </ul>
            </div>

        </nav>
    </div>
}




export default NavBar;
