import React, {Component} from 'react';
import {DayPilot, DayPilotCalendar, DayPilotNavigator} from "daypilot-pro-react";
import {ResourceGroups} from "./ResourceGroups";

class Calendar extends Component {

  constructor(props) {
    super(props);

    this.calendarRef = React.createRef();
    this.datePickerRef = React.createRef();

    this.state = {
      viewType: "Resources",
      timeRangeSelectedHandling: "Enabled",
      onTimeRangeSelected: async args => {
        const modal = await DayPilot.Modal.prompt("Create a new lesson:", "Event 1" );
        this.calendar.clearSelection();
        if (!modal.result) { return; }
        this.calendar.events.add({
          start: args.start,
          end: args.end,
          id: DayPilot.guid(),
          resource: args.resource,
          text: modal.result
        });
        console.log(this.calendar.events);
      },
      eventDeleteHandling: "Update",
      onEventClick: async args => {
        const modal = await DayPilot.Modal.prompt("Update event text:", args.e.text());
        if (!modal.result) { return; }
        const e = args.e;
        e.data.text = modal.result;
        this.calendar.events.update(e);
      },
      onHeaderClick: async args => {
        const modal = await DayPilot.Modal.prompt("Resource name:", args.column.name);
        if (!modal.result) { return; }
        args.column.data.name = modal.result;
        this.calendar.update();
      }
    };
  }

  get calendar() {
    return this.calendarRef.current.control;
  }

  get datePicker() {
    return this.datePickerRef.current.control;
  }

  componentDidMount() {
    const date = new Date();
    const today = date.getFullYear() + "-" + (date.getMonth()+1 < 10 ? "0":"")+(date.getMonth()+1) + "-" + date.getDate();
    this.datePicker.select(today);
  }

  loadGroups() {
      const data = [
        { name: "Days", id: "days", resources: [
            {name: "Sunday", id: "D1"},
            {name: "Monday", id: "D2"},
            {name: "Tuesday", id: "D3"},
            {name: "Wednesday", id: "D4"},
            {name: "Thursday", id: "D5"},
            {name: "Friday", id: "D6"},
            {name: "Saturday", id: "D7"},
          ]
        },


      ];
      return data;
  }

  groupChanged(group) {

    const columns = group.resources;

    const events = [];

    this.calendar.update({columns, events});

  }

  next() {
    const current = this.datePicker.selectionDay;
    const updated = current.addDays(1);
    this.datePicker.select(updated);
  }

  previous() {
    const current = this.datePicker.selectionDay;
    const updated = current.addDays(-1);
    this.datePicker.select(updated);
  }

  render() {

    return (
      <div className={"wrap"}>   
        <div className={"calendar"}>
          <div className={"toolbar"}>    
            <ResourceGroups onChange={args => this.groupChanged(args.selected)} items={this.loadGroups()}></ResourceGroups>
            <span>Day:</span>
            <button onClick={ev => this.previous()}>Previous</button>
            <button onClick={ev => this.next()}>Next</button>
          </div>

          <DayPilotCalendar
            {...this.state}
            ref={this.calendarRef}
          />
        </div>
        <div className={"left"}>

          <DayPilotNavigator
            selectMode={"Day"}
            showMonths={1}
            skipMonths={1}
            onTimeRangeSelected={ args => {
              this.calendar.update({
                startDate: args.day
              });
            }}
            ref={this.datePickerRef}
          />
        </div>
      </div>
    );
  }
}

export default Calendar;
