import React, { useState } from "react";
import Card from "../Components/Card"
const axios = require('axios').default;

function Home() {
    const [coachs, setCoaches] = useState([]);
    if (coachs.length === 0) {
        getCoachList();
    }

    console.log(coachs);


    function getCoachList() {
        const params = {
            isCoach: true,
        }
        const URL = 'http://localhost:4000/users/user'; // for Local
        axios.get(URL, { params: params })
            .then((res) => {
                if (res.data === "No Coach") {
                    console.log("No Coach");
                } else {
                    setCoaches(res.data);
                }
            });
    }

    return <div className="">
        <div className="home-form card card-body center">
            <h1>Welcome to Poolit</h1>
        </div>
        <div className="cards">
            {coachs.forEach(coach => {
                <div className="coach-card">
                    <Card coachName={coach.username.split("@")[1]} coachDays="" coachStyles="" coachHours="" />
                </div>
            })}
            <div className="coach-card">
                <Card coachName="Yotam" coachDays="" coachStyles="" coachHours="" />
            </div>
            <div className="coach-card">
                <Card coachName="Yoni" />
            </div>
            <div className="coach-card">
                <Card coachName="Joni" />
            </div>
        </div>
    </div>
}


export default Home;
